mapper.update:
update tb_board
   set read_count = read_count + 1
 where board_no 	= #{boardNo}

mapper.insert:
insert
	into tb_board
		 (
		   board_no
		  ,regist_emp
		  ,modify_emp
		  ,title
		  ,content
		  ,category
		  ,status
		 )
values 
		(
		  seq_bdno.nextval
		 ,#{registEmp}
		 ,#{modifyEmp}
		 ,#{title}

		 ,#{content}
		 ,(
		 	select upper_code
		 	  from tb_group
		 	 where group_code like 'TEAM%'
		 	 	 and code = #{category}
		  )
		 ,#{status}
		)

mapper.update:
update tb_board
   set 
   	    title = #{title}
   	   ,content = #{content}
   	   ,category = #{category}
   	   ,status = #{status}
   	   ,modify_date = sysdate
   	   ,modify_emp = #{modifyEmp}
 where board_no = #{boardNo}

